dmdApp.controller("dmdProfileCtrl",function(a,d,b,c){a.username=b.currentUser.username;a.email=b.currentUser.email;a.firstName=b.currentUser.firstName;a.lastName=b.currentUser.lastName;a.update=function(){var e={username:a.username,email:a.email,firstName:a.firstName,lastName:a.lastName};if(a.password&&a.password.length>0){e.password=a.password}d.updateCurrentUser(e).then(function(){c.notify("User profile has been updated")},function(f){c.error(f)})}});